import styled from "styled-components";

export const Wrap = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
  background-color: #fff;
  width: 100%;
  /* padding: 80px 30px 0; */
`;

export const Container = styled.div`
  display: flex;
  /* justify-content: center; */
  flex-wrap: wrap;
  column-gap: 2%;
  row-gap: 2.5rem;
  /* max-width: 1276px; */
  width: 100%;
  /* gap: 50px 20px; */
`;

export const Title = styled.p`
  font-size: 1.25rem;
  font-weight: 400;
  margin: 23px 0;
  text-align: center;
`;
