import { ContentContainer } from "src/commonStyles";
import { Cards } from "./cards";
import { Container, Title, Wrap } from "./styled";
import CompanyCard from "./CatalogCard/CatalogCard";
import { IStore } from "src/store/interfaces/store.interface";
import { useSelector } from "react-redux";
import { Navigate, redirect, useNavigate } from "react-router-dom";
import { useEffect } from "react";
import store from "src/store";
import { setUser } from "src/store/actions";
import UsersService from "src/utils/api/services/Users";
import { JWTPayload } from "src/utils/interfaces/jwt-payload.interface";
import { getItem } from "src/utils/local-storage";
import jwt from "jwt-decode";
import { useDispatch } from "react-redux";
import { APP_ROUTES } from "src/utils/constants";

export default function Home() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  console.log("DASHBOARD");

  const cards = Cards.map((card) => (
    <Catal key={card.id} imgUrl={card.imgUrl} title={card.title} />
  ));

  // useEffect(() => {
  //   (async function () {
  //     const token = getItem("token");
  //     if (!token) return navigate(APP_ROUTES.LOGIN, { replace: true });

  //     const payload: JWTPayload = jwt(token);
  //     const user = await UsersService.getUser(+payload.sub);

  //     if (!user.id) return;

  //     dispatch(setUser(user));
  //   })();
  // }, [dispatch, navigate]);

  return (
    <Wrap>
      <ContentContainer>
        <Title>Каталог компаний</Title>
        <Container>{cards}</Container>
      </ContentContainer>
    </Wrap>
  );
}

export const dashboardLoader = async () => {
  const token = getItem("token");
  if (!token) return redirect(APP_ROUTES.LOGIN);

  const payload: JWTPayload = jwt(token);
  const user = await UsersService.getUser(+payload.sub);

  if (!user.id) return null;

  store.dispatch(setUser(user));

  return user;
};
