//!  do not use

export const SvgWrapper = styled.div`
  position: relative;
  top: -1px;
  width: 36px;
  height: 36px;

  /* @media (max-width: 510px) {
    & {
      width: 24px;
      height: 24px;
    }
  } */
`;

export const Title = styled.p`
  font-size: 1.5rem;
  margin: 0;

  &::first-letter {
    text-transform: capitalize;
  }

  /* @media (max-width: 1303px) {
    & {
      font-size: 1.25rem;
    }
  } */
`;
