import {
  Button,
  ErrorMessage,
  Input,
  Page,
  Wrapper,
} from "src/commonStyles";
import { Container, Content, Form, Inner, Title } from "./styled";
import { useForm, SubmitHandler } from "react-hook-form";

interface IFormInput {
  name: string;
  surname: string;
  email: string;
  login: string;
  password: string;
  passwordConfirm: string;
}

export default function Register() {
  const {
    register,
    formState: { errors },
    handleSubmit,
  } = useForm<IFormInput>();
  const onSubmit: SubmitHandler<IFormInput> = (data) => {
    console.log(data);
  };

  return (
    <Page>
      <Content>
        <Wrapper>
          <Title>Регистрация</Title>
          <Form onSubmit={handleSubmit(onSubmit)}>
            <Inner>
              <Input
                {...register("name", {
                  required: { value: true, message: "Введите имя" },
                })}
                placeholder="Имя"
                $width="350px"
                autoComplete="off"
              />
              <ErrorMessage>{errors.login?.message}</ErrorMessage>
            </Inner>
            <Inner>
              <Input
                {...register("password", {
                  required: { value: true, message: "Введите пароль" },
                })}
                placeholder="Пароль"
                $width="455px"
                autoComplete="off"
              />
              <ErrorMessage>{errors.password?.message}</ErrorMessage>
            </Inner>
            <Container>
              <Button type="submit" $width="126px" $white>
                Войти
              </Button>
              <Button $width="210px">Регистрация</Button>
            </Container>
          </Form>
        </Wrapper>
      </Content>
    </Page>
  );
}
