import { Button, ErrorMessage, Input } from "src/commonStyles";
import { Container, Content, Form, Page, Title, Wrapper } from "./styled";
import { useForm, SubmitHandler } from "react-hook-form";

interface IFormInput {
  login: string;
  password: string;
}

export default function Login() {
  const { register, formState: {error},handleSubmit } = useForm<IFormInput>();
  const onSubmit: SubmitHandler<IFormInput> = (data) => {
    console.log(data);
  };

  return (
    <Page>
      <Content>
        <Wrapper>
          <Title>Вход</Title>
          <Form onSubmit={handleSubmit(onSubmit)}>
            <Input
              {...register("login", {
                required: { value: true, message: "Введите логин" },
              })}
              placeholder="Логин"
              $width="455px"
            />
            <ErrorMessage>{error}</ErrorMessage>
            <Input
              {...register("password", { required: true })}
              placeholder="Пароль"
              $width="455px"
            />
            <Container>
              <Button type="submit" $width="126px" $white>
                Войти
              </Button>
              <Button $width="210px">Регистрация</Button>
            </Container>
          </Form>
        </Wrapper>
      </Content>
    </Page>
  );
}
