import styled from "styled-components";
import { LogoImg } from "./assets";

export const Wrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;

  .width100proc {
    width: 100%;
  }
`;

export const Page = styled(Wrapper)`
  width: 100vw;
  min-height: 100vh;
  height: 100%;
  width: 100%;
  padding: 30px 0;
`;

export const ContentContainer = styled.div`
  width: 85%;
  max-width: 1276px;
  margin: 0 auto;
`;

export const Input = styled.input<{
  $width?: string;
  $height?: string;
  $disabled?: boolean;
}>`
  font-size: 2.5rem;
  padding: 0 20px;
  width: ${(props) => props.$width || "auto"};
  height: ${(props) => props.$height || "80px"};
  border: 4px solid #c4fff9;
  border-radius: 20px;
  background-color: #07beb8;
  transition: 0.1s linear;

  cursor: ${(props) => (props.$disabled ? "not-allowed" : "text")};
  /* color: ${(props) => (props.$disabled ? "#9d9d9d" : "#000")}; */

  &::placeholder {
    /* Chrome, Firefox, Opera, Safari 10.1+ */
    color: #c4fff9;
    opacity: 1; /* Firefox */
    text-align: center;
  }

  &:-ms-input-placeholder {
    /* Internet Explorer 10-11 */
    color: #c4fff9;
    text-align: center;
  }

  &::-ms-input-placeholder {
    /* Microsoft Edge */
    color: #c4fff9;
    text-align: center;
  }

  &:hover {
    border-color: #fff;
  }

  &:focus::placeholder {
    color: #fff;
  }

  &:focus {
    outline: none;
    color: #fff;
    border-color: #fff;
  }
`;

export const ErrorMessage = styled.p`
  display: flex;
  align-items: center;
  font-size: 1.5rem;
  color: #c4fff9;
`;

export const Button = styled.button<{
  $white?: boolean;
  $light?: boolean;
  $width?: string;
}>`
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 2.5rem;
  height: 80px;
  min-width: 250px;
  /* padding: 17px 24px 14px; */
  border: 4px solid #07beb8;
  border-radius: 20px;
  background-color: ${(props) =>
    props.$white ? "#fff" : props.$light ? "#3DCCC7" : "#07BEB8"};

  color: ${(props) => (props.$white ? "#07BEB8" : "#fff")};
  width: ${(props) => props.$width || "auto"};
  transition: 0.1s linear;

  &:hover {
    background-color: ${(props) =>
      props.$white ? "#07BEB8" : props.$light ? "#fff" : "#3DCCC7"};
    color: ${(props) =>
      props.$white ? "#fff" : props.$light ? "#07BEB8" : "#fff"};
  }

  &:active {
    background-color: ${(props) =>
      props.$white ? "#3DCCC7" : props.$light ? "#07BEB8" : "#fff"};
    color: ${(props) =>
      props.$white ? "#fff" : props.$light ? "#fff" : "#07BEB8"};
  }

  /* @media (max-width: 1500px) {
    & {
      max-width: 18rem;
    }
  } */
`;

export const Search = styled.input`
  font-size: 1.25rem;
  padding: 0 20px;
  max-width: 600px;
  width: 100%;
  height: 40px;
  border: 4px solid var(--light);
  border-radius: 20px;
  background-color: #07beb8;
  transition: 0.1s linear;

  &::placeholder {
    /* Chrome, Firefox, Opera, Safari 10.1+ */
    color: var(--light);
    opacity: 1; /* Firefox */
    text-align: center;
  }

  &:-ms-input-placeholder {
    /* Internet Explorer 10-11 */
    color: var(--light);
    text-align: center;
  }

  &::-ms-input-placeholder {
    /* Microsoft Edge */
    color: var(--light);
    text-align: center;
  }

  &:hover {
    border-color: #fff;
  }

  &:focus::placeholder {
    color: #fff;
  }

  &:focus {
    outline: none;
    color: #fff;
    border-color: #fff;
  }

  /* @media (max-width: 1500px) {
    & {
      max-width: 50rem;
      width: 100%;
    }
  }

  @media (max-width: 1303px) {
    & {
      max-width: 100%;
      width: 100%;
    }
  } */
`;

export const Logo = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-width: 40px;
  min-height: 40px;
  max-width: 40px;
  max-height: 40px;
  color: var(--light);
  font-size: 2rem;
  font-weight: bold;
  border-radius: 40px;
  border: 2px solid var(--light);
  background-color: var(--dark);

  /* @media (max-width: 768px) {
    & {
      display: none;
    }
  } */
`;

export const Title = styled.p`
  font-size: 1.5rem;
  font-weight: 400;
  margin: 0 auto 1.5rem;
  text-align: center;
  color: var(--dark);

  &::first-letter {
    text-transform: capitalize;
  }
`;

//! do not use
//! do not use
