import styled from "styled-components";

export const Wrap = styled.div`
  display: flex;
  justify-content: space-between;
  background-color: #fff;
  height: 100%;
  width: 100%;
  max-width: 1920px;
  padding: 60px;
  margin: 0 auto;
`;

export const InfoContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  min-height: 940px;
  max-width: 500px;
  width: 100%;
  gap: 30px;
`;

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 1126px;
  width: 100%;
  gap: 20px;
`;

export const Calendar = styled.table`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: var(--dark);
  border: 4px solid var(--light);
  border-radius: 20px;
  width: 1150px;
  font-size: 4rem;
  /* margin-top: 100px; */

  & th,
  & td {
    display: flex;
    justify-content: center;
    align-items: center;
    min-width: 100px;
    min-height: 100px;
  }
  & tr {
    display: flex;
    justify-content: center;
  }
`;
