import styled from "styled-components";

export const Wrap = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 1115px;
  width: 100%;
  height: 100%;
  margin: 0 auto;
`;

export const Title = styled.p`
  font-size: 1.75rem;
  margin: 0 auto;
`;

export const Hr = styled.hr`
  margin: 31px 0;
  border: 1px solid #000;
`;
