import { Button, ErrorMessage, Input, Page, Wrapper } from "src/commonStyles";
import { Container, Content, Form, Inner, Title } from "./styled";
import { useForm, SubmitHandler } from "react-hook-form";
import { redirect } from "react-router-dom";

interface IFormInput {
  login: string;
  password: string;
}

export default function Login() {
  const {
    register,
    formState: { errors },
    handleSubmit,
    reset,
  } = useForm<IFormInput>();
  const onSubmit: SubmitHandler<IFormInput> = (data) => {
    console.log(data);
    reset({
      login: "",
      password: "",
    });
    redirect("/home");
  };

  return (
    <Page>
      <Content>
        <Wrapper>
          <Title>Вход</Title>
          <Form onSubmit={handleSubmit(onSubmit)}>
            <Inner>
              <Input
                {...register("login", {
                  required: { value: true, message: "Введите логин" },
                  pattern: {
                    value: /^[A-Za-z0-9]+$/,
                    message: "Только латинские буквы и цифры",
                  },
                  maxLength: { value: 50, message: "Максимум 50 знаков" },
                })}
                placeholder="Логин"
                $width="455px"
                autoComplete="off"
              />
              <ErrorMessage>{errors.login?.message}</ErrorMessage>
            </Inner>
            <Inner>
              <Input
                {...register("password", {
                  required: { value: true, message: "Введите пароль" },
                  pattern: {
                    value: /^[A-Za-z0-9]+$/,
                    message: "Только латинские буквы и цифры",
                  },
                  maxLength: { value: 50, message: "Максимум 50 знаков" },
                })}
                placeholder="Пароль"
                $width="455px"
                autoComplete="off"
              />
              <ErrorMessage>{errors.password?.message}</ErrorMessage>
            </Inner>
            <Container>
              <Button type="submit" $width="126px" $white>
                Войти
              </Button>
              <Button $width="210px">Регистрация</Button>
            </Container>
          </Form>
        </Wrapper>
      </Content>
    </Page>
  );
}
