import React from "react";
import {
  Container,
  Img,
  Reviews,
  Text,
  TextArea,
  Title,
  Wrap,
} from "./styled";
import { Wrap as Wrapp } from "./Review/styled";
import { ReviewsList } from "./reviews";
import Review from "./Review/Review";
import { Button } from "src/commonStyles";

interface Props {
  imgUrl: string;
  title: string;
  description: string;
  address: string;
}

export default function Company(props: Props) {
  const reviews = ReviewsList.map((review) => (
    <Review
      userImgUrl={review.userImgUrl}
      userName={review.userName}
      reviewText={review.reviewText}
      id={review.id}
    />
  ));

  return (
    <Wrap>
      <Title>{props.title}</Title>
      <Hr />
      <Img $backgroundImg={props.imgUrl} />
      <Text>{props.description}</Text>
      <Text>{props.address}</Text>
      <Reviews>Отзывы</Reviews>
      <Container>
        {reviews}
        <Wrapp>
          <TextArea placeholder="Введите комментарий" />
          <Button>Отправить</Button>
        </Wrapp>
      </Container>
    </Wrap>
  );
}
