import { useState } from "react";
import {
  Container,
  Form,
  Img,
  InfoContainer,
  Reviews,
  TextArea,
  TextAreaBox,
  Wrap,
} from "./styled";
import { Wrap as Wrapp } from "../Companies/Review/styled";
import Review from "../Companies/Review/Review";
import {
  Button,
  Title,
  StarsContainer,
  StarWrapper,
  ContentContainer,
  ErrorMessage,
  AvatarImg,
  Text,
} from "src/commonStyles";
import { redirect } from "react-router-dom";
import { getItem } from "src/utils/local-storage";
import { APP_ROUTES } from "src/utils/constants";
import UsersService from "src/utils/api/services/Users";
import { JWTPayload } from "src/utils/interfaces/jwt-payload.interface";
import jwt from "jwt-decode";
import store from "src/store";
import { setActivatedCompany, setUser } from "src/store/actions";
import CompaniesService from "src/utils/api/services/Companies";
import { IStore } from "src/store/interfaces/store.interface";
import { useSelector } from "react-redux";
import styled, { ThemeProvider } from "styled-components";
import { IReview } from "src/utils/interfaces/review.interface";
import { SubmitHandler, useForm } from "react-hook-form";
import { DatePicker, RangePicker, theme } from "react-trip-date";

interface IFormInput {
  review: string;
}

const ReviewsList: IReview[] = [
  {
    id: 1,
    text: "Все понравилось. И персонал, и месторождение, и печеньки с кофе, и чистота в помещениях. Молодцы. Уходить не хотелось",
    user: {
      id: -1,
      name: "Анна Коляго",
      imgUrl:
        "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8NXx8dXNlcnxlbnwwfHwwfHx8MA%3D%3D&w=1000&q=80",
    },
  },
  {
    id: 2,
    text: "Персонал хамло!",
    user: {
      id: -1,
      name: "Наталья Хамлова",
      imgUrl: "https://oir.mobi/uploads/posts/2020-01/1579144434_1-1.jpg",
    },
  },
  {
    id: 3,
    text: "Очень крутая компания! Поддержка суперская, молниеносная, спасибо большое ребятам за такой сервис и высшую культуру обслуживания клиентов! Так держать! Всем советуем)",
    user: {
      id: 1,
      name: "Top Gangster",
      imgUrl: "https://eduenglishpassport.sru.ac.th/assets/images/users/1.jpg",
    },
  },
];

export default function Doctor() {
  const user = useSelector((store: IStore) => store.auth.user);
  const doctor = useSelector((store: IStore) => store.doctors.activatedDoctor);
  const [reviews, setReviews] = useState<IReview[]>(ReviewsList);

  const reviewsItems = reviews.map((review) => (
    <Review key={review.id} data={review} />
  ));

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
  } = useForm<IFormInput>();

  const onSubmit: SubmitHandler<IFormInput> = (data) => {
    setReviews((prevState) => {
      if (!user) return prevState;

      const newState = [...prevState];
      newState.push({
        id: prevState[prevState.length - 1].id + 1,
        text: `${data.review}`,
        user: {
          id: user.id,
          name: user.firstName + " " + user.lastName,
          imgUrl:
            "https://images.unsplash.com/photo-1633332755192-727a05c4013d?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxzZWFyY2h8Mnx8dXNlcnxlbnwwfHwwfHx8MA%3D%3D&w=1000&q=80",
        },
      });
      reset({ review: "" });
      return newState;
    });
  };

  const handleResponsive = (setNumberOfMonth) => {
    let width = document.querySelector(".tp-calendar").clientWidth;
    if (width > 900) {
      setNumberOfMonth(3);
    } else if (width < 900 && width > 580) {
      setNumberOfMonth(2);
    } else if (width < 580) {
      setNumberOfMonth(1);
    }
  };

  const Day = ({ day }) => {
    return (
      <>
        <p className="date">{day.format("DD")}</p>
        <p className="date">7</p>
      </>
    );
  };
  const onchange = (date) => console.log(date);

  return (
    <Wrap>
      <InfoContainer>
        <Title>Doctor information</Title>
        <AvatarImg
          $backgroundImg={
            doctor?.imgUrl ||
            "https://nordin.by/wp-content/uploads/2022/12/doctor-full.jpg"
          }
          $margin_bottom="20px"
        />
        <Text>{doctor?.fullName}</Text>
        <Text>{doctor?.specialization}</Text>
        <Text>{doctor?.workTime}</Text>
      </InfoContainer>
      <Container>
        <ThemeProvider theme={theme}>
          <DatePicker
            handleChange={onChange}
            selectedDays={["2019-11-06"]} //initial selected days
            jalali={false}
            numberOfMonths={3}
            numberOfSelectableDays={3} // number of days you need
            disabledDays={["2019-12-02"]} //disabeld days
            responsive={handleResponsive} // custom responsive, when using it, `numberOfMonths` props not working
            disabledBeforToday={true}
            disabled={false} // disable calendar
            dayComponent={Day} //custom day component
            titleComponent={Title} // custom title of days
          />
        </ThemeProvider>
      </Container>

      {/* <Reviews>Отзывы</Reviews>
        <Container>
          {reviewsItems}
          <Wrapp>
            <Form onSubmit={handleSubmit(onSubmit)}>
              <TextAreaBox>
                <TextArea
                  {...register("review", {
                    required: { value: true, message: "Заполните поле" },
                  })}
                  placeholder="Enter комментарий"
                />
                <ErrorMessage>{errors.review?.message}</ErrorMessage>
              </TextAreaBox>
              <Button type="submit">Отправить</Button>
            </Form>
          </Wrapp>
        </Container> */}
    </Wrap>
  );
}

const ReviewsScoreContainer = styled.div`
  display: flex;
  column-gap: 0.5rem;
  align-items: center;
  justify-content: center;
  margin: 1.5rem 0;
`;

export const doctorLoader = async ({ params }: any) => {
  const token = getItem("token");
  if (!token) return redirect(APP_ROUTES.LOGIN);

  const payload: JWTPayload = jwt(token);
  let [user, company] = await Promise.all([
    UsersService.getUser(+payload.sub),
    CompaniesService.getCompany({ id: params.id }),
  ]);
  if (!user.id) return null;

  store.dispatch(setUser(user));
  store.dispatch(setActivatedCompany(company));

  return user;
};
