import styled from "styled-components";

export const Wrap = styled.div`
  display: flex;
  justify-content: space-between;
  background-color: #fff;
  height: 100%;
  width: 100%;
  max-width: 1920px;
  padding: 60px;
  margin: 0 auto;
`;

export const InfoContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  min-height: 940px;
  max-width: 500px;
  width: 100%;
  gap: 30px;
`;

export const Img = styled.div<{ $backgroundImg: string }>`
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 25px;
  max-width: 800px;
  width: 100%;
  background-image: url(${(props) => props.$backgroundImg});
  background-position: center;
  background-repeat: no-repeat;
  background-size: contain;
  height: 300px;
  margin: 0 auto;
`;

export const Reviews = styled.div`
  display: flex;
  justify-content: center;
  width: 250px;
  font-size: 1.1rem;
  border-bottom: 1px solid #000;
  padding: 2px;
  margin: 0.65rem auto 2.9rem;
`;

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 1126px;
  width: 100%;
  gap: 20px;
`;

export const TextArea = styled.textarea<{ $width?: string; $height?: string }>`
  font-size: 2.5rem;
  padding: 0 20px;
  width: ${(props) => props.$width || "auto"};
  height: ${(props) => props.$height || "80px"};
  border: 4px solid var(--light);
  border-radius: 20px;
  background-color: var(--dark);
  transition: 0.1s linear;

  &::placeholder {
    /* Chrome, Firefox, Opera, Safari 10.1+ */
    color: var(--light);
    opacity: 1; /* Firefox */
    text-align: center;
    align-self: centerl
  }

  &:-ms-input-placeholder {
    /* Internet Explorer 10-11 */
    color: var(--light);
    text-align: center;
  }

  &::-ms-input-placeholder {
    /* Microsoft Edge */
    color: var(--light);
    text-align: center;
  }

  &:hover {
    border-color: #fff;
  }

  &:focus::placeholder {
    color: #fff;
  }

  &:focus {
    outline: none;
    color: #fff;
    border-color: #fff;
  }
`;

export const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

export const TextAreaBox = styled.div`
  display: flex;
  flex-direction: column;
  width: 100%;
  padding-right: 20px;
`;
