import { Button, Logo, Search } from "src/commonStyles";
import { ContentContainer } from "./styled";
import { Wrap } from "./styled";
import { Link, useNavigate } from "react-router-dom";
import { removeItem } from "src/utils/local-storage";
import { useDispatch } from "react-redux";
import { resetUser } from "src/store/actions";
import { APP_ROUTES } from "src/utils/constants";

export default function Header() {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const onLogoutHandler = () => {
    removeItem("token");
    dispatch(resetUser());
    navigate(APP_ROUTES.LOGIN, { replace: true });
  };

  const onPersonalCabinetHandler = () => {
    navigate("");
  };

  return (
    <Wrap>
      <ContentContainer>
        <Link
          style={{ outline: "none", textDecoration: "none", width: "226px" }}
          to={APP_ROUTES.CATALOG}
        >
          <Logo>ComReviev</Logo>
        </Link>
        <Search placeholder="Найти" />
        <Link
          style={{ outline: "none", textDecoration: "none", color: "#000" }}
          to="companies/add"
        >
          Добавить объект
        </Link>
        <Button $width="258px" onClick={onPersonalCabinetHandler}>
          Личный кабинет
        </Button>
        <Link style={{ outline: "none", textDecoration: "none" }} to="/login">
          <Button $width="150px" $white onClick={onLogoutHandler}>
            Выйти
          </Button>
        </Link>
      </ContentContainer>
    </Wrap>
  );
}
