import styled from "styled-components";

export const Wrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`;

export const Page = styled(Wrapper)`
  background-color: #bbbbbb;
  width: 100vw;
  height: 100vh;
`;

export const Content = styled(Wrapper)`
  max-width: 540px;
  width: 100%;
  height: 398px;
  background-color: #fff;
  border-radius: 15px;
`;

export const Title = styled(Wrapper)`
  font-size: 2rem;
  margin
`;

export const Form = styled.form`
  display: flex;

  flex-direction: column;
  gap: 22px;
  height: 100%;
`;

export const Input = styled.input`
  font-size: 1.5rem;
  padding: 18px 14px 19px;
  width: 455px;
  height: 60px;
  border: 1px solid #bbbbbb;
  border-radius: 15px;
  background-color: #eae9e9;

  &::placeholder {
    /* Chrome, Firefox, Opera, Safari 10.1+ */
    color: #bbbbbb;
    opacity: 1; /* Firefox */
  }

  &:-ms-input-placeholder {
    /* Internet Explorer 10-11 */
    color: #bbbbbb;
  }

  &::-ms-input-placeholder {
    /* Microsoft Edge */
    color: #bbbbbb;
  }
`;

export const Container = styled.div`
  display: flex;
  justify-content: space-evenly;
`;

export const Button = styled.button<{ $white?: boolean; $width?: string }>`
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 1.5rem;
  height: 60px;
  padding: 17px 24px 14px;
  border: ${(props) => (props.$white ? "5px solid #4C7BFF" : "none")};
  border-radius: 15px;
  background-color: ${(props) => (props.$white ? "#fff" : "#4c7bff")};
  color: ${(props) => (props.$white ? "#000" : "#fff")};
  width: ${(props) => props.$width || "auto"};
`;
