import { Button, Label } from "src/commonStyles";
import { Box, Container, Form, TextArea, Wrap } from "./styled";
import { Hr, Title } from "src/commonStyles";
import { Input } from "src/commonStyles";
import { SubmitHandler, useForm } from "react-hook-form";

interface IFormInput {
  category: string;
  title: string;
  description: string;
  address: string;
  logo: any;
}

export default function AddCompany() {
  const {
    register,
    formState: { errors },
    handleSubmit,
    reset,
  } = useForm<IFormInput>();

  const onSubmit: SubmitHandler<IFormInput> = (data) => {
    console.log(data);
    reset({category: "",
      title: "",
      description: "",
      address: string,
      logo: any,})
  }

  return (
    <Wrap>
      <Title>Добавить объект</Title>
      <Hr />
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Container>
          <Label>Категория</Label>
          <Input {...register("category")} $width="757px" $height="75px" />
        </Container>
        <Container>
          <Label>Название</Label>
          <Input {...register("title")} $width="757px" $height="75px" />
        </Container>
        <Container>
          <Label>Описание</Label>
          <TextArea {...register("description")} />
        </Container>
        <Container>
          <Label>Адрес</Label>
          <Input {...register("address")} $width="757px" $height="75px" />
        </Container>
        <Container>
          <Label>Логотип</Label>
          <Button {...register("logo")} as="input" type="file" />
          {/* Добавить фото */}
        </Container>
        <Box>
          <Button type="submit">Отправить на обработку</Button>
        </Box>
      </Form>
    </Wrap>
  );
}
