import styled from "styled-components";

export const Wrap = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
  background-color: #fff;
  width: 100%;
  padding: 0 30px;
`;

export const Container = styled.div`
  display: flex;
  justify-content: center;
  flex-wrap: wrap;
  max-width: 1478px;
  width: 100%;
  gap: 33px 40px;
`;

export const Title = styled.p`
  font-size: 1.5rem;
  margin: 0 auto 31px;;
`;
