import { createSlice, PayloadAction } from "@reduxjs/toolkit";
import { IAuthState } from "../interfaces/auth.interface";
import { IUser } from "src/utils/interfaces/user.interface";
import { IHospitalState } from "../interfaces/catalog.interface";
import { IHospital } from "src/utils/interfaces/catalog.interface";
import { IReviewState } from "../interfaces/reviews.interface";
import { IReview } from "src/utils/interfaces/review.interface";

const InitialState: IReviewState = {
  data: [],
  activatedHospital: null,
};

export const reviewsSlice = createSlice({
  name: "reviews",
  initialState: InitialState,
  reducers: {
    setReviews: (state, { payload }: PayloadAction<IReview[]>) => {
      state.data = payload;
    },
    resetReviews: (state) => {
      state.data = [];
    },
    setActivatedHospital: (state, { payload }: PayloadAction<IHospital>) => {
      state.activatedHospital = payload;
    },
    resetActivatedHospital: (state) => {
      state.activatedHospital = null;
    },
  },
});

export default reviewsSlice;
