import React from "react";
import { Container, Hr, Img, Reviews, Text, Title, Wrap } from "./styled";
import { ReviewsList } from "./reviews";
import Review from "./Review/Review";

interface Props {
  imgUrl: string;
  title: string;
  description: string;
  address: string;
}

export default function Company(props: Props) {
  const reviews = ReviewsList.map((review) => (
    <Review userImgUrl={review.userImgUrl} userName={review.userName} reviewText={review} />
  ));

  return (
    <Wrap>
      <Title>{props.title}</Title>
      <Hr />
      <Img $backgroundImg={props.imgUrl} />
      <Text>{props.description}</Text>
      <Text>{props.address}</Text>
      <Reviews>Отзывы</Reviews>
      <Container>{reviews}</Container>
    </Wrap>
  );
}
