import React from "react";
import { Container, Description, UserImg, UserName, Wrap } from "./styled";
import { StarWrapper, StarsContainer } from "src/commonStyles";
import { IReview } from "src/utils/interfaces/review.interface";
import styled from "styled-components";

interface Props {
  data: IReview;
}

export default function Review({ data }: Props) {
  return (
    <Wrap>
      <CardImg
        $background={
          props.hospitalCard.imgUrl ||
          "https://static.vecteezy.com/system/resources/previews/004/493/181/original/hospital-building-for-healthcare-background-illustration-with-ambulance-car-doctor-patient-nurses-and-medical-clinic-exterior-free-vector.jpg"
        }
      />
      <Container>
        <ReviewHeader>
          <UserImg $backgroundImg={data.user.imgUrl} />
          <UserName>{data.user.name}</UserName>
        </ReviewHeader>
        <Description>{data.text}</Description>
      </Container>
    </Wrap>

    /* 
      
       */
  );
}

const ReviewHeader = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 1rem;
`;
