import styled from "styled-components";

export const Wrap = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 10px;
  border-radius: 25px;
  box-shadow: 0px 4px 4px 0px #00000040;
  width: 304px;
`;

export const Img = styled.img`
  width: 36px;
  height: 36px;
`;

export const Title = styled.p`
  font-size: 1.5rem;
`;
