import { Button, ErrorMessage, Label } from "src/commonStyles";
import { Box, Container, Form, TextArea, Wrap } from "./styled";
import { Hr, Title } from "src/commonStyles";
import { Input } from "src/commonStyles";
import { SubmitHandler, useForm } from "react-hook-form";

interface IFormInput {
  category: string;
  title: string;
  description: string;
  address: string;
  logo: any;
}

export default function AddCompany() {
  const {
    register,
    formState: { errors },
    handleSubmit,
    reset,
  } = useForm<IFormInput>();

  const onSubmit: SubmitHandler<IFormInput> = (data) => {
    console.log(data);
    reset({
      category: "",
      title: "",
      description: "",
      address: "",
      logo: undefined,
    });
  };

  return (
    <Wrap>
      <Title>Добавить объект</Title>
      <Hr />
      <Form onSubmit={handleSubmit(onSubmit)}>
        <Container>
          <Label>Категория</Label>
          <Input
            {...register("category", {
              required: { value: true, message: "Заполните поле" },
            })}
            $width="757px"
            $height="75px"
          />
          <ErrorMessage>{errors.category?.message}</ErrorMessage>
        </Container>
        <Container>
          <Label>Название</Label>
          <Input
            {...register("title", {
              required: { value: true, message: "Заполните поле" },
            })}
            $width="757px"
            $height="75px"
          />
          <ErrorMessage>{errors.title?.message}</ErrorMessage>
        </Container>
        <Container>
          <Label>Описание</Label>
          <TextArea
            {...register("description", {
              required: { value: true, message: "Заполните поле" },
            })}
          />{" "}
          <ErrorMessage>{errors.category?.message}</ErrorMessage>
        </Container>
        <Container>
          <Label>Адрес</Label>
          <Input
            {...register("address", {
              required: { value: true, message: "Заполните поле" },
            })}
            $width="757px"
            $height="75px"
          />
        </Container>
        <Container>
          <Label>Логотип</Label>
          <Button
            {...register("logo", {
              required: { value: true, message: "Заполните поле" },
            })}
            as="input"
            type="file"
          />
          {/* Добавить фото */}
        </Container>
        <Box>
          <Button type="submit">Отправить на обработку</Button>
        </Box>
      </Form>
    </Wrap>
  );
}
