import styled from "styled-components";

export const Wrap = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 1127px;
  width: 100%;
  height: 100%;
  margin: 0 auto;
  padding: 30px 30px 0;
`;

export const Form = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 1000px;
  width: 100%;
  height: 100%;
`;

export const Container = styled.div`
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
  gap: 10px;
  max-width: 1000px;
  height: 100%;
  width: 100%;
`;

export const TextArea = styled.textarea`
  height: 184px;
  max-width: 757px;
  width: 100%;
  padding: 18px 14px 19px;
  background-color: #eae9e9;
  border: 1px solid #bbbbbb;
  border-radius: 15px;
  font-size: 1.25rem;
  resize: none;

  &::placeholder {
    /* Chrome, Firefox, Opera, Safari 10.1+ */
    color: #bbbbbb;
    opacity: 1; /* Firefox */
  }

  &:-ms-input-placeholder {
    /* Internet Explorer 10-11 */
    color: #bbbbbb;
  }

  &::-ms-input-placeholder {
    /* Microsoft Edge */
    color: #bbbbbb;
  }
`;
