import styled from "styled-components";

export const Wrap = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
  background-color: #fff;
  width: 100%;
  padding: 1.5rem;
`;

export const InfoContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  min-height: 1370px;
  max-width: 500px;
  width: 100%;
`;

export const Container = styled.div`
  display: flex;
  justify-content: flex-start;
  flex-wrap: wrap;
  max-width: 1478px;
  width: 100%;
  gap: 33px 40px;
`;

export const HospitalImg = styled.div<{ $backgroundImg: string }>`
  display: flex;
  justify-content: center;
  align-items: center;
  max-width: 500px;
  max-height: 500px;
  width: 100%;
  background-image: url(${(props) => props.$backgroundImg});
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  height: 100%;
  margin-bottom: 20px;
`;
